import React, { useState, useContext } from 'react';
import { useHistory } from "react-router-dom";
import axios from "axios";
import UserContext from "../../context/userContext";
import ErrorNotice from "../../components/misc/ErrorNotice";
function Register () {
const [email, setEmail] = useState();
const [password, setPassword] = useState();
const [passwordCheck, setPasswordCheck] = useState();
const [name, setName] = useState();
const [surname, setSurname] = useState();
const [address, setAddress] = useState();
const [phone, setPhone] = useState();
const [birth, setBirth] = useState();
const [error, setError] = useState();
const { setUserData } = useContext(UserContext);
const history = useHistory();
const submit = async (e) => {
e.preventDefault();
try{
const newUser = {email, password, passwordCheck, surname, name, address, phone, birth};
await axios.post("http://localhost:5000/users/register", newUser);
const loginResponse = await axios.post("http://localhost:5000/users/login", {
email, password, 
});
setUserData({
token: loginResponse.data.token,
user: loginResponse.data.user
});
localStorage.setItem("auth-token", loginResponse.data.token);
history.push("/");
} catch(err) {
err.response.data.msg && setError(err.response.data.msg)
}
};
return (
<div className="register">
    <div class="card">
<h2>Registro</h2>
{error && <ErrorNotice message={error} clearError={() => setError(undefined)} />}
<form onSubmit={submit}>
<label>Nombre: </label>
<input type="text" id="name" onChange={e => setName(e.target.value)}/>
<label>Apellidos: </label>
<input type="text" id="surname" onChange={e => setSurname(e.target.value)}/>
<label>Dirección: </label>
<input type="text" id="address" onChange={e => setAddress(e.target.value)}/>
<label>Fecha nacimiento: </label>
<input type="date" id="birth" onChange={e => setBirth(e.target.value)}/>
<label>Teléfono: </label>
<input type="text" id="phone" onChange={e => setPhone(e.target.value)}/>
<label>Email: </label>
<input type="email" id="email" onChange={e => setEmail(e.target.value)}/>
<label>Contraseña: </label>
<input type="password" id="password" onChange={e => setPassword(e.target.value)}/>
<input type="password" placeholder="Confirma contraseña" onChange={e => setPasswordCheck(e.target.value)}/>
<input type="submit" value="Registrar" className="btn btn-primary" />
</form>
</div>
</div>
);
}
export default Register;
